package com.windsing.myTest;

public class DoubleToString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Double d = 32.00;
		Double d1 = 32.00;
		
		System.out.println(d.toString());//32.0
		if(d == d1){
			System.out.println("=========");//32.0
		}else{
			System.out.println("!!!!!!!!=========");//32.0
		}
	}

}
