package com.windsing.myTest;

public class TestAnd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 2; //10
		int b = 3; //11
		int c = a&b;  
		/*
		 * 根据与运算符的运算规律，只有两个位都是1，结果才是1
		 * 10
		 * 11
		 * ----
		 * 10  =  2
		 */
		System.out.println(c);

	}

}
